using System.Windows;
using System.Windows.Input;

namespace WpfApp1
{
    /// <summary>
    /// About.xaml 的交互逻辑
    /// </summary>
    public partial class About : Window
    {
        public About()
        {
            WindowStartupLocation = WindowStartupLocation.CenterScreen;//屏幕居中
            InitializeComponent();
            this.ShowInTaskbar = false;//隐藏任务栏图片
            this.Topmost = true;//窗口置顶
        }
        private void bilibili(object sender, MouseButtonEventArgs e)//跳转到作者的BILIBILI主页
        {
            System.Diagnostics.Process.Start("https://space.bilibili.com/96876893");
        }
        private void github(object sender, MouseButtonEventArgs e)//跳转到作者的Github主页
        {
            System.Diagnostics.Process.Start("https://github.com/JonyanDunh");
        }

        private void GPL(object sender, MouseButtonEventArgs e)//跳转到GNU通用公共许可证主页
        {
            System.Diagnostics.Process.Start("https://www.gnu.org/licenses/gpl-3.0-standalone.html");
        }
        private void microsoft(object sender, MouseButtonEventArgs e)//跳转到WPF主页
        {
            System.Diagnostics.Process.Start("https://docs.microsoft.com/en-us/dotnet/framework/wpf/");
        }
        private void hide(object sender, MouseButtonEventArgs e)//隐藏窗口
        {
            this.Hide();
        }
    }
}
