using System;
using System.Windows;
using System.Windows.Input;
using System.Windows.Media.Imaging;

namespace WpfApp1
{
    /// <summary>
    /// Float.xaml 的交互逻辑
    /// </summary>
    public partial class Float : Window
    {
        MainWindow window = new MainWindow();//主窗口
        int i = 0;//判断主窗口是否打开
        public Float()
        {
            InitializeComponent();
            this.ShowInTaskbar = false;//隐藏任务栏图标
            double ScreenWidth = System.Windows.SystemParameters.PrimaryScreenWidth;//获取屏幕宽度
            this.Top = (SystemParameters.WorkArea.Height) * 9 / 10;//设置悬浮窗Y轴位置在屏幕的1/10
            this.Left = SystemParameters.WorkArea.Width * 1 / 70;//设置悬浮窗X轴位置在屏幕的1/70
            this.Topmost = true;
        }
        private void click(object sender, System.Windows.Input.MouseButtonEventArgs e)
        {
            if (i == 1)//打开了主窗口
            {

                img.Source = new BitmapImage(new Uri("pack://application:,,,/resource/swopen.png"));//变换图标
                window.Hide();//隐藏主窗口
                i = 0;//主窗口为隐藏
            }
            else//没有打开主窗口
            {
                img.Source = new BitmapImage(new Uri("pack://application:,,,/resource/swhide.png"));//变换图标
                window.Show();//显示主窗口
                i = 1;//主窗口为显示
            }
        }

        private void Window_MouseMove(object sender, System.Windows.Input.MouseEventArgs e)//鼠标移动悬浮球
        {
            if (e.LeftButton == MouseButtonState.Pressed)
            {
                this.DragMove();
            }
        }
    }
}
